select pres.sn_lr, drs.pcod, drs.fam_v, drs.im_v, drs.ot_v, dpos.name_vd, d_job, dcats.category, pers.im, pers.fam, pers.ot, pers.ss, pers.w, pers.dr, pres.sn_pol, pcats.name_kat, dss.name_ds, srs.source, percs.percentage, dnpns.name_mnn_r, dforms.name_lf, pres.doz_ls, dunits.name_dls, pduras.duration, pres.n_doz, pres.weight_vol, pres.date_vr
from persons pers, prescriptions pres, doctors drs, polyclinics pls, doctor_positions dpos, doctor_categories dcats, person_categories pcats, diseases dss, funding_sources srs, payment_percentages percs, drug_forms dforms, drug_dosage_units dunits, prescription_durations pduras, drug_nonproprietary_names dnpns
where pres.person_id = pers.id and pres.doctor_id = drs.id and drs.polyclinic_id = pls.id and drs.position_id = dpos.id and drs.category_id = dcats.id and pres.category_id = pcats.id and pres.disease_id = dss.id and pres.source_id = srs.id and pres.percentage_id = percs.id and pres.form_id = dforms.id and pres.unit_id = dunits.id and pres.duration_id = pduras.id and pres.nonproprietary_id = dnpns.id and pres.status_id = 2 and upper(pls.m_namef) like '%ОБЛ%ОНК%' and pres.date_vr > '01.01.2010';